mapper.select:
SELECT *, (SELECT u_name FROM user WHERE u_no=comment.u_no) u_name FROM comment 
WHERE boardno=#{boardno} AND tablename=#{tablename} 
ORDER BY regdate DESC

mapper.insert:
INSERT INTO comment (
tablename, content, boardno , regdate , a_no 
) values(
#{tablename}, #{content}, #{boardno} , NOW() ,#{a_no} 
)
